        },
      };
    },
    computed: {
      swiper() {
        return this.$refs.myswiper.$swiper;
      },
    },
    methods: {
      // 鼠标移入停止轮播
      stopAutoPlay() {
        this.swiperOption.autoplay && this.swiper.autoplay.stop();
      },
      // 鼠标移出开始轮播
      startAutoPlay() {
        this.swiperOption.autoplay && this.swiper.autoplay.start();
      },
    },
  });

